import React, { useState } from 'react';

export const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = () => {
    console.log('Login attempt:', { username, password });
  };

  return (
    <>
      <style>{`
        .whisperwire-login-page * {
          margin: 0;
          padding: 0;
          box-sizing: border-box;
        }

        .whisperwire-login-page {
          font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, sans-serif;
          line-height: 1.6;
        }

        .whisperwire-login-container {
          min-height: 100vh;
          display: flex;
        }

        /* Left side - Login Form */
        .whisperwire-login-section {
          flex: 1;
          display: flex;
          align-items: center;
          justify-content: center;
          background-color: white;
          padding: 3rem 2rem;
        }

        .whisperwire-login-content {
          width: 100%;
          max-width: 28rem;
        }

        /* Logo */
        .whisperwire-logo {
          margin-bottom: 2rem;
        }

        .whisperwire-logo-container {
          display: flex;
          align-items: center;
          font-size: 1.5rem;
          font-weight: bold;
          color: #111827;
        }

        .whisperwire-logo-icon {
          width: 2rem;
          height: 2rem;
          background-color: #111827;
          border-radius: 0.375rem;
          margin-right: 0.75rem;
          display: flex;
          align-items: center;
          justify-content: center;
        }

        .whisperwire-logo-icon-inner {
          width: 1rem;
          height: 1rem;
          background-color: white;
          border-radius: 0.125rem;
        }

        /* Welcome Text */
        .whisperwire-welcome {
          margin-bottom: 2rem;
        }

        .whisperwire-welcome h1 {
          font-size: 1.875rem;
          font-weight: bold;
          color: #111827;
          margin-bottom: 0.5rem;
        }

        .whisperwire-welcome p {
          color: #6b7280;
        }

        /* Form */
        .whisperwire-form {
          display: flex;
          flex-direction: column;
          gap: 1.5rem;
        }

        .whisperwire-form-group {
          display: flex;
          flex-direction: column;
        }

        .whisperwire-form-label {
          font-size: 0.875rem;
          font-weight: 500;
          color: #374151;
          margin-bottom: 0.5rem;
        }

        .whisperwire-form-input {
          width: 100%;
          padding: 0.75rem;
          border: 1px solid #d1d5db;
          border-radius: 0.5rem;
          font-size: 1rem;
          transition: all 0.2s ease;
        }

        .whisperwire-form-input:focus {
          outline: none;
          box-shadow: 0 0 0 2px #3b82f6;
          border-color: #3b82f6;
        }

        .whisperwire-form-input::placeholder {
          color: #9ca3af;
        }

        .whisperwire-sign-in-btn {
          width: 100%;
          background-color: #000;
          color: white;
          padding: 0.75rem 1rem;
          border: none;
          border-radius: 0.5rem;
          font-size: 1rem;
          font-weight: 500;
          cursor: pointer;
          transition: background-color 0.2s ease;
        }

        .whisperwire-sign-in-btn:hover {
          background-color: #1f2937;
        }

        .whisperwire-signup-link {
          text-align: center;
          font-size: 0.875rem;
          color: #6b7280;
        }

        .whisperwire-signup-link a {
          color: #2563eb;
          text-decoration: none;
          font-weight: 500;
        }

        .whisperwire-signup-link a:hover {
          color: #1d4ed8;
        }

        /* Right side - Image */
        .whisperwire-image-section {
          flex: 1;
          position: relative;
          background-color: black;
          display: flex;
          align-items: center;
          justify-content: center;
          overflow: hidden;
        }

        .whisperwire-background-image {
          position: absolute;
          top: 0;
          left: 0;
          right: 0;
          bottom: 0;
          width: 100%;
          height: 100%;
          object-fit: cover;
          object-position: center;
        }

        /* Text overlay */
        .whisperwire-text-overlay {
          position: absolute;
          bottom: 4rem;
          left: 2rem;
          right: 2rem;
          text-align: center;
          color: white;
          z-index: 20;
        }

        .whisperwire-text-overlay h2 {
          font-size: 1.875rem;
          font-weight: bold;
          margin-bottom: 1rem;
          line-height: 1.2;
        }

        /* Responsive Design */
        @media (max-width: 768px) {
          .whisperwire-login-container {
            flex-direction: column;
          }

          .whisperwire-login-section {
            padding: 2rem 1rem;
          }

          .whisperwire-image-section {
            min-height: 300px;
          }

          .whisperwire-welcome h1 {
            font-size: 1.5rem;
          }

          .whisperwire-text-overlay {
            bottom: 2rem;
            left: 1rem;
            right: 1rem;
          }

          .whisperwire-text-overlay h2 {
            font-size: 1.5rem;
          }

          .whisperwire-placeholder-content {
            padding: 1rem;
            margin: 1rem;
          }

          .whisperwire-placeholder-content h3 {
            font-size: 1.25rem;
          }

          .whisperwire-placeholder-content p {
            font-size: 1rem;
          }
        }

        @media (max-width: 480px) {
          .whisperwire-login-section {
            padding: 1.5rem 1rem;
          }

          .whisperwire-welcome h1 {
            font-size: 1.25rem;
          }

          .whisperwire-logo-container {
            font-size: 1.25rem;
          }

          .whisperwire-form-input {
            padding: 0.625rem;
          }

          .whisperwire-sign-in-btn {
            padding: 0.625rem 1rem;
          }

          .whisperwire-text-overlay h2 {
            font-size: 1.25rem;
          }
        }
      `}</style>
      <div className="whisperwire-login-page">
        <div className="whisperwire-login-container">
          {/* Left side - Login Form */}
          <div className="whisperwire-login-section">
            <div className="whisperwire-login-content">
              {/* Logo */}
              <div className="whisperwire-logo">
                <div className="whisperwire-logo-container">
                  <div className="whisperwire-logo-icon">
                    <div className="whisperwire-logo-icon-inner"></div>
                  </div>
                  WhisperWire
                </div>
              </div>

              {/* Welcome Text */}
              <div className="whisperwire-welcome">
                <h1>Welcome back</h1>
                <p>Please enter your details</p>
              </div>

              {/* Form */}
              <div className="whisperwire-form">
                {/* Username Input */}
                <div className="whisperwire-form-group">
                  <label htmlFor="whisperwire-username" className="whisperwire-form-label">
                    Username
                  </label>
                  <input
                    type="text"
                    id="whisperwire-username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    className="whisperwire-form-input"
                    placeholder="Enter your username"
                    required
                  />
                </div>

                {/* Password Input */}
                <div className="whisperwire-form-group">
                  <label htmlFor="whisperwire-password" className="whisperwire-form-label">
                    Password
                  </label>
                  <input
                    type="password"
                    id="whisperwire-password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    className="whisperwire-form-input"
                    placeholder="Enter your password"
                    required
                  />
                </div>

                {/* Sign In Button */}
                <button
                  type="button"
                  onClick={handleSubmit}
                  className="whisperwire-sign-in-btn"
                >
                  Sign in
                </button>

                {/* Sign Up Link */}
                <div className="whisperwire-signup-link">
                  Don't have an account?{' '}
                  <a href="#">Sign up</a>
                </div>
              </div>
            </div>
          </div>

          {/* Right side - Image */}
          <div className="whisperwire-image-section">
            {/* 🔥 PASTE YOUR IMAGE PATH HERE 🔥 */}
            <img 
              src="./placeholderimage.jpg" 
              alt="Login background" 
              className="whisperwire-background-image" 
            />
            
            {/* Text overlay */}
            <div className="whisperwire-text-overlay">
              <h2>
                Secure by Design. Private by Nature.
              </h2>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Login;
