import { useState } from 'react';
import { useAuth } from "../../context_rout"
export const Home = () => {
  const { socketRef } = useAuth()
  const [mymsg, setmymsg] = useState([])
  const [msg, setmsg] = useState('')
  const [incoming, setincoming] = useState([]);
  const [u, setUser] = useState([])
  socketRef.current.on("incoming", (msg) => {
    setincoming(prev => [...prev, msg]);
  });
  socketRef.current.on("users",(users)=>{
    users.forEach()
  })
  const onmsgChange = (e) => {
    setmsg(e.target.value)
  }
  const onSendClick = () => {
    setmymsg(prev => [...prev, msg]);
    socketRef.current.emit("chat message", msg);
    setmsg('');
  }

  return (
    <div className="App"> 
      <h1>This is chat app</h1>
      <input onChange={onmsgChange} value={msg} placeholder='Enter your message' />
      <button onClick={onSendClick}>Send</button>
      <div className='Users'>{u.map((user)=>user)}</div>
      <div className='Messages'>
        <div className='sent'>
          <h3>Sent</h3>
          {mymsg.map(msg => <li key={msg}>{msg}</li>)}
        </div>
        <div className='recived'>
          <h3>Recived</h3>
          {incoming.map(msg => <li key={msg}>{msg}</li>)}
        </div>
      </div>
    </div>
  );
}
