import './App.css';
import { useRef, useState } from 'react';
import { io } from 'socket.io-client';

function App() {
  const socketRef = useRef(null);
  const [mymsg, setmymsg] = useState([])
  const [msg, setmsg] = useState('')
  const [incoming, setincoming] = useState([]);
  const onConnectClick = () => {
    if (!socketRef.current) {
      socketRef.current = io('https://6fb3-223-233-74-186.ngrok-free.app', {
        path: "/socket.io/",
        transports: ["websocket"]
      });

      socketRef.current.on('connect', () => {
        console.log('Connected:', socketRef.current.id);
      });
      socketRef.current.on("incoming", (msg) => {
        setincoming(prev => [...prev, msg]);
      });
    }
  }
  const onmsgChange = (e) => {
    setmsg(e.target.value)
  }
  const onSendClick = () => {
    setmymsg(prev => [...prev, msg]);

    socketRef.current.emit("chat message", msg);
    setmsg('');
  }

  return (
    <div className="App">
      <h1>This is chat app</h1>
      <button onClick={onConnectClick}>Connect</button>
      <input onChange={onmsgChange} value={msg} placeholder='Enter your message' />
      <button onClick={onSendClick}>Send</button>
      <div className='Messages'>
        <div className='sent'>
          <h3>Sent</h3>
          {mymsg.map(msg => <li key={msg}>{msg}</li>)}
        </div>
        <div className='recived'>
          <h3>Recived</h3>
          {incoming.map(msg => <li key={msg}>{msg}</li>)}
        </div>
      </div>
    </div>
  );
}
export default App;
